      e = e || window.event;
      let re = /^(0|[1-9][0-9]*|[1-9]\d*\.\d*|0\.\d*|\-(0|[1-9]*)|\-\d*\.\d*)$/;
      el.value && !re.test(el.value) ? el.value = old_value : old_value = el.value;
    });
  }
});

